import React from 'react';
import { Link } from "react-router-dom";
import './css/login.css'

class Login extends React.Component {
  constructor () {
    super()
    this.state={
      loginFailed: false
    }
  }

  handleClick = (e) => {
    e.preventDefault();
   const username = e.target.name.value;
   const password = e.target.password.value;
   fetch('http://localhost:7777/login',{
     method: 'Post',
     headers: {
       "Content-Type": "application/json"
     },
     body: JSON.stringify({
      username: username,
      password: password
     })
   })
   .then(res => res.json())
   .then(data => {
     if(data.message != undefined){
      this.setState({loginFailed: true})
     } else {
       localStorage.setItem('jwt', data.jwt)
     }
   })
    // if (this.stateisLoggedin) {
    //   this.props.history.push("/game");
    // } else {

    // }
  }

  render () {
    return (
      <div id="login">
        {this.state.loginFailed ? <p>Invalid Username or Password </p> : null}
        <form id="login-form" onSubmit={this.handleClick}>
          <input name="name" className="input-box" type="text" placeholder="name"/>
          <br />
          <input name="password" className="input-box" type="password" placeholder="password"/>
          <br />
          <input
            className="input-box"
            id="button"
            type="submit"
            value="submit"
          />
        </form>
        <Link id="signup" to="/signup">
          Create New User
        </Link>
      </div>
    );
  }
}

export default Login;
